import React from 'react';
import App from './App.js';

export default class Parent extends React.Component {
    constructor(props) {
        super(props);
        this.setState(true);
    }

    changeSize() {
        this.setState(!this.state.big);
        console.log("=========== big: " + this.state.big);
    }

    render() {
        return (
            <div>
                <App
                    prop1={"This is the App div..."}
                    bignotsmall={this.state.big}
                    clickHandler={() => this.changeSize()} />
            </div>
        );
    };
}
