import './app.css'
function App(props) {
  let newtext1 = props.prop1;
  let newtext2 = "     bignotsmall: " + props.bignotsmall;
  let big = true;
  function changeSize(){
    big = !big;
    console.log("===========");
  }
  function renderSize() {
    return (
      <div className={big ? "bigmaindiv" : "smallmaindiv"}>
        {newtext1} <br /> {newtext2}
        <br />
        <br />
        <br />
        <div  className="positionButton">
        <button onClick={()=>changeSize()}>Alternate Size</button>
        </div>
      </div>
    );
  }

  return (
    renderSize() //props.bignotsmall)
  );
}

export default App;
