import { useTranslation, Trans } from 'react-i18next';
import React, { useState } from 'react';
import SearchItem from './searchitem.js';
import Logout from './logout.js';
import './menubar.css'
//import './i18n.js';

export default function Menubar(props) {
    const { t, i18n } = useTranslation();
    // const langs = {
    //     en: { nativeName: 'English' },
    //     ptbr: { nativeName: 'Português' }
    // };

    const [menuOption, setMenuOption] = useState("Search");

    function changeMenuItem() {
        if (menuOption === "Search") setMenuOption("Busca");
        else setMenuOption("Search");
    }

    // function changeLang(){
    //     if (i18n.language === langs.ptbr.nativeName)
    //         i18n.changeLanguage(langs.en.nativeName);
    //     else
    //         i18n.changeLanguage(langs.ptbr.nativeName);
    // }

    return (
        <div className="menubar">
            <div className="titletext">BiblioPDF</div>

            <div className="language">
            <div    className="menuitem"
                    onClick={()=>i18n.resolvedLanguage==="en"?i18n.changeLanguage("pt"):i18n.changeLanguage("en")}>
                            {i18n.resolvedLanguage === "pt"?"...in English, please!":"...em Português, por favor!"}
            </div>
          </div>

            <div className="menuitems">
                <div className="busca">
                    <SearchItem
                        option={menuOption}
                        language={"PORT"}
                        clickHandler={() => changeMenuItem()} />
                </div>
                <div className="intervalo"></div>
                <div className="sair"><Logout /></div>
            </div>
        </div>
    );
}
