import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

i18n
  // detect user language
  // learn more: https://github.com/i18next/i18next-browser-languageDetector
  .use(LanguageDetector)
  // pass the i18n instance to react-i18next.
  .use(initReactI18next)
  // init i18next
  // for all options read: https://www.i18next.com/overview/configuration-options
  .init({
    debug: true,
    fallbackLng: 'pt',
    interpolation: {
      escapeValue: false, // not needed for react as it escapes by default
    },
    resources: {
      en: {
        translation: {
            appPageTexts: {
                part1: "...em Português, por favor!",
                part2: 'Another english text!',
                part3: 'Search',
                part4: 'Logout',
                part5: 'Search',
                part6: 'Cataloging',
                part7: 'About'
              },
            about:{
              paragraph01: 'BiblioPDF is a React/Java web application for handling electronic bibliographic materials, as papers, books and even code.',  
              paragraph02: 'The frontend will be developed using REACT and the backend will ultimately be a REST based API implemented with '+
                ' Spring (a Java backend framework) and Spring-JPA on PostgreSQL.',  
              paragraph03: 'The development process is based on Gitlab (free tier) with a continuous integration and continuous deployment (CI/CD) discipline that includes automated testing.',  
              paragraph04: 'The deploy will be made in a cloud service (AWS or other) or use Docker on a traditional provider like Digital Ocean.',  
              paragraph05: 'Although this app should be useful, it will mostly serve as a technology demonstration.',  
              paragraph06: 'The project is being conducted by Jorge L.S. Leão (jorge.leao@ufrj.br, solo for the moment).',  
              paragraph07: 'The software will be available as a free open software.',
              paragraph08: 'The project started on january 27th, 2022. It has not yet produced a working version nor been deployed.'
            }  
        }
      },
      pt:{
          translation:{
            appPageTexts: {
                part1: "...in English, please!",
                part2: 'Outro texto em Português!',
                part3: 'Busca',
                part4: 'Sair',
                part5: 'Busca',
                part6: 'Catalogação',
                part7: 'Sobre'
            },
            about: {
              paragraph01: 'BiblioPDF é uma aplicação React/Java para armazenar, buscar e recuperar materiais bibliográficos eletrônicos, como papers, livros e até código de programas de computador',  
              paragraph02: 'O frontend vai ser desenvolvido em REACT e o backend vai ser, na sua versão final, uma implementação de uma API REST em Spring,'+
                            ' (um framework Java para backend), com SpringJPA e PostgreSQL.',  
              paragraph03: 'O processo de desenvolvimento está sendo feito com o Gitlab (free tier) com uma disciplina de CI/CD que inclui testes automatizados.',  
              paragraph04: 'A implantação será feita em um serviço de cloud (AWS ou outro) ou em um sistema como o Docker num provedor tradicional como o Digital Ocean.',  
              paragraph05: 'Embora esta aplicação seja útil por si mesma, ela deve servir sobretudo como uma demonstração das tecnologias mencionadas acima.',  
              paragraph06: 'O projeto está sendo conduzido por Jorge L.S.Leão (jorge.leao@ufrj.br, sozinho por enquanto).',  
              paragraph07: 'O software será disponibilizado como um software aberto e gratuito.',
              paragraph08: 'O projeto iniciou-se em 27 de janeiro de 2022. Ele ainda não produziu uma versão funcional nem foi implantado.'
            }
          }
      }
    }
  });

export default i18n;
