import React, { useState } from 'react';
import LanguageChooser from './languagechooser.js'
import SearchItem from './searchitem.js';
import Logout from './logout.js';
import './menubar.css'

export default function Menubar(props) {
    const [lang,setLang] = useState("pt");
    const [menuOption,setMenuOption] = useState("Search");
    function changeLang(){
        if(lang==="pt") setLang("en");
        else setLang("pt");
    }
    function changeMenuItem(){
        if(menuOption==="Search") setMenuOption("Busca");
        else setMenuOption("Search");
    }

    return (
        <div className="menubar">
            <div className="titletext">BiblioPDF</div>
            <div className="language">
                <LanguageChooser
                            language={lang}
                            clickHandler={() => changeLang()}/>
            </div>
            <div className="menuitems">
                <div className="busca">
                    <SearchItem
                            option={menuOption}
                            language={lang}
                            clickHandler={() => changeMenuItem()}/>
                </div>
                <div className="intervalo"></div>
                <div className="sair"><Logout language={lang}/></div>
            </div>
        </div>
    );
}
