import './app.css'
function App(props) {
  const newline = "<br />";
  const newtext = props.prop1 + newline +"     bignotsmall: " + props.bignotsmall;
  function renderSize(big){
    if(big)
      return (
        <div className="bigmaindiv">
          {newtext}
        </div>
      );
    else
    return (
      <div className="smallmaindiv">
        {newtext}
      </div>
    );
}

  return (
    renderSize(props.bignotsmall) 
  );
}

export default App;
