import { useTranslation, Trans } from 'react-i18next';
import React, { useState } from 'react';
import LanguageChooser from './languagechooser.js'
import SearchItem from './searchitem.js';
import Logout from './logout.js';
import './menubar.css'

export default function Menubar(props) {
    const { t, i18n } = useTranslation();
    function changeLang() {
        i18n.resolvedLanguage === "en" ? i18n.changeLanguage("pt") : i18n.changeLanguage("en")
    }

    const [menuOption, setMenuOption] = useState("search");
    function changeMenuItem() {
        if (menuOption === "search") {
            setMenuOption("cat");
            //--- Use router here
            console.log("=== Cataloging ===");
        } else {
            setMenuOption("search");
            //--- Use router here
            console.log("=== Search ===");
        }
    }

    function logOut() {
        console.log("=== LogOut ===");
    }

    return (
        <div className="menubar">
            <div className="titletext">BiblioPDF</div>

            <div className="language">
                <LanguageChooser clickHandler={() => changeLang()} />
            </div>


            <div className="menuitems">
                <div className="search-catalog">
                    <div className="search">
                        Busca
                    </div>
                    <div className="catalog">
                        Catalogação
                    </div>
                </div>
                <div className="logout"><Logout clickHandler={() => logOut()} /></div>
            </div>
        </div >
    );
}
