import './app.css'
function App(props) {
  const newtext1 = props.prop1;
  const newtext2 = "     bignotsmall: " + props.bignotsmall;
  function renderSize(big){
    if(big)
      return (
        <div className="bigmaindiv">
        {newtext1} <br /> {newtext2}
        </div>
      );
    else
    return (
      <div className="smallmaindiv">
        {newtext1} <br /> {newtext2}
      </div>
    );
}

  return (
    renderSize(props.bignotsmall) 
  );
}

export default App;
